    (function executeRule(current, previous /*null when async*/ ) {

        // Add your code here


        try {

            //gs.addErrorMessage("Error is:" + current.request_type);
            // current.setAbortAction(true);
            //return;
            var username = gs.getProperty('x_58872_needit.API Username');
            var password = gs.getProperty('x_58872_needit.API Password');
            var r = new sn_ws.RESTMessageV2('x_58872_needit.Update Subscriber Name', 'Update Subscriber Name');
            r.setStringParameter("firstName", current.first_name);
            r.setStringParameter("lastName", current.last_name);
            r.setStringParameter("msisdn", current.phone_number);
            r.setStringParameter("username", username);
            r.setStringParameter("password", password);
            var response = r.executeAsync();
            response.waitForResponse(30);
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            var res = JSON.parse(response.getBody());
            if (res.result == "error") {
                gs.addErrorMessage("Response is:" + res.resultMessage);
                current.setAbortAction(true);
            }
        } catch (ex) {
            var message = ex.message;
            gs.addErrorMessage("Error is:" + message);
            current.setAbortAction(true);
        }


    })(current, previous);

})(current, previous);
